got') { ?> <script type="text/javascript" src="LoginCSS/js/forgot.js"></script><?php } ?> <script>$(window).load(function(){$("#status").fadeOut(350);$("#preloader").delay(350).fadeOut("slow");$("body").delay(350).css({overflow:"visible"})});</script><?= $StingCMS->SuiviGoogle(); ?></body></html>